const handleSubmit = async (e) => {
  e.preventDefault();

  if (!form.name || form.name.trim() === "") {
    alert("Product name is required");
    return;
  }

  setLoading(true);

  try {
    const data = new FormData();
    data.append("name", form.name);
    data.append("description", form.description || "");
    data.append("price", form.price || 0);
    data.append("quantity", form.quantity || 0);

    if (form.image) data.append("image", form.image);

    await api.post("", data, {
      headers: { "Content-Type": "multipart/form-data" },
    });

    setForm({ name: "", description: "", price: "", quantity: "", image: null });
    onAdded();
  } catch (err) {
    console.error("Axios error:", err);
    if (err.response) {
      let message =
        err.response.data?.message || err.response.data || JSON.stringify(err.response.data);
      alert(`Backend error (${err.response.status}): ${message}`);
    } else if (err.request) {
      alert("No response from server. Please check your backend.");
    } else {
      alert(`Request error: ${err.message}`);
    }
  } finally {
    setLoading(false);
  }
};
